package com.yang.user.mynewsdemo.ui.adapter;import android.content.Context;import android.support.v4.widget.SwipeRefreshLayout;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.yang.user.mynewsdemo.R;import com.yang.user.mynewsdemo.ui.bean.GirlBean;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;/** * Created by User on 2017/3/16. */public class GankGirlAdapter extends RecyclerView.Adapter{    private LayoutInflater inflater;    private OnItemClickListener onItemClickListener;    private List<GirlBean.ResultsBean> resultsBeanList = null;    private Context context;    public GankGirlAdapter(Context context,List<GirlBean.ResultsBean> resultsBeanList){        this.context = context;        this.resultsBeanList = resultsBeanList;        inflater = LayoutInflater.from(context);    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = inflater.inflate(R.layout.item_gank_girl,parent,false);        GankViewHolder gankViewHolder = new GankViewHolder(view);        return gankViewHolder;    }    @Override    public void onBindViewHolder(final RecyclerView.ViewHolder holder, int position) {        final GankViewHolder gankViewHolder = (GankViewHolder) holder;        Glide.with(context).load(resultsBeanList.get(position).getUrl())                .error(R.mipmap.ic_launcher)                .placeholder(R.mipmap.ic_launcher).into(gankViewHolder.girImageview);        gankViewHolder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if(onItemClickListener != null) {                    View shareView = v.findViewById(R.id.iv_girl);                    Log.e("yang","" + gankViewHolder.getAdapterPosition());                    onItemClickListener.onItemClickListener(gankViewHolder.getAdapterPosition(),shareView);                }            }        });    }    @Override    public int getItemCount() {        return resultsBeanList.size();    }    class GankViewHolder extends RecyclerView.ViewHolder{        @BindView(R.id.iv_girl)        public ImageView girImageview;        public GankViewHolder(View itemView) {            super(itemView);            ButterKnife.bind(this, itemView);        }    }    public void setOnItemClickListener(OnItemClickListener onItemClickListener) {        this.onItemClickListener = onItemClickListener;    }    public interface OnItemClickListener {        void onItemClickListener(int position,View view);    }}