package com.yang.user.mynewsdemo.ui.api;import com.yang.user.mynewsdemo.ui.bean.DailyBean;import com.yang.user.mynewsdemo.ui.bean.HotListBean;import com.yang.user.mynewsdemo.ui.bean.SectionListBean;import com.yang.user.mynewsdemo.ui.bean.ThemeListBean;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Path;import rx.Observable;/** * Created by User on 2017/3/13. */public interface ZhihuApis {    String HOST = "http://news-at.zhihu.com/api/";    /**     * 最新日报     */    @GET("{page}/news/latest")    Call<DailyBean> getDailyList(@Path("page") int page);    /**     * 主题日报     *     */    @GET("{page}/themes")    Call<ThemeListBean> getThemelistbean(@Path("page") int page);    /**    * 专栏日报    */    @GET("{page}/sections")    Call<SectionListBean> getSelectlistbean (@Path("page")int page);    /**     *     * 热门日报     */   @GET("{page}/news/hot")    Call<HotListBean> getHotlistbean (@Path("page")int page);}