package com.yang.user.mynewsdemo.ui.fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.design.widget.TabLayout;import android.support.v4.app.Fragment;import android.support.v4.view.ViewPager;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.yang.user.mynewsdemo.R;import com.yang.user.mynewsdemo.ui.adapter.ZhihuMainAdapter;import com.yang.user.mynewsdemo.ui.fragment.ganktabfragment.AndroidFragment;import com.yang.user.mynewsdemo.ui.fragment.ganktabfragment.GirlFragment;import com.yang.user.mynewsdemo.ui.fragment.ganktabfragment.IosFragment;import com.yang.user.mynewsdemo.ui.fragment.ganktabfragment.WebFragment;import com.yang.user.mynewsdemo.ui.fragment.tabfragment.DailyFragment;import java.util.ArrayList;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;import me.yokeyword.fragmentation.SupportFragment;/** * Created by User on 2017/3/9. */public class GankMainFragment extends Fragment {    private View view;    @BindView(R.id.tab_zhihu_main)    TabLayout mTabLayout;    @BindView(R.id.vp_zhihu_main)    ViewPager mViewPager;    String[] tabTitle = new String[]{"Android", "iOS", "前端", "福利"};    List<Fragment> fragments = new ArrayList<Fragment>();    ZhihuMainAdapter mAdapter;    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        view = inflater.inflate(R.layout.fragment_zhihu, container, false);        ButterKnife.bind(this, view);        initData();        return view;    }    private void initData() {        fragments.add(new AndroidFragment());        fragments.add(new IosFragment());        fragments.add(new WebFragment());        fragments.add(new GirlFragment());        mAdapter = new ZhihuMainAdapter(getChildFragmentManager(), fragments);        mViewPager.setAdapter(mAdapter);        mTabLayout.addTab(mTabLayout.newTab().setText(tabTitle[0]));        mTabLayout.addTab(mTabLayout.newTab().setText(tabTitle[1]));        mTabLayout.addTab(mTabLayout.newTab().setText(tabTitle[2]));        mTabLayout.addTab(mTabLayout.newTab().setText(tabTitle[3]));        mTabLayout.setupWithViewPager(mViewPager);        mTabLayout.getTabAt(0).setText(tabTitle[0]);        mTabLayout.getTabAt(1).setText(tabTitle[1]);        mTabLayout.getTabAt(2).setText(tabTitle[2]);        mTabLayout.getTabAt(3).setText(tabTitle[3]);    }}