package com.yang.user.mynewsdemo.ui.fragment;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;/** * Created by User on 2017/3/10. */public class LikeFragment extends Fragment{    private TextView view;    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        ViewGroup p = (ViewGroup) view.getParent();        if(p != null){            p.removeAllViewsInLayout();        }        return view;    }    @Override    public void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        LayoutInflater inflater = getActivity().getLayoutInflater();        view = new TextView(getActivity());        view.setTextSize(20);        view.setText("LikeFragment");    }}